var levelup = require('levelup');
var sqldown = require('./');
var crypto = require('crypto');
var db = levelup('postgres://localhost/sqldown?table=_calvin_leveldown_test_db_', {db: sqldown});


function push(cb) {
var a = [];
var i = -1;
while (++i < 100) {
  a.push({
    key: i,
    type: 'put',
    value: crypto.randomBytes(30)
  })
}
db.batch(a, cb);
}
push(function (err) {
  if (err) {
    return console.log(err);
  }
  push(function (err) {
    if (err) {
      return console.log(err);
    }
    push(function (err) {
      if (err) {
        return console.log(err);
      }
      push(function (err) {
        if (err) {
          return console.log(err);
        }
        push(function (err) {
          if (err) {
            return console.log(err);
          }
          db.close();
        })
      })
    })
  })
})
